export class CarListComponent {
  constructor(service, primengConfig, messageService, confirmationService) {
    this.service = service;
    this.primengConfig = primengConfig;
    this.messageService = messageService;
    this.confirmationService = confirmationService;
    this.page = 0;
    this.pageSize = 10;
    this.name = '';
    this.statuses = [true, false];
  }

  ngOnInit() {
    // console.log('data');
    this.paginate();
    this.getGearList();
    this.getBrandList();
    this.sortOptions = [{
      label: 'Price High to Low',
      value: '!price'
    }, {
      label: 'Price Low to High',
      value: 'price'
    }];
  }

  getGearList() {
    this.service.getGearList().subscribe(data => {
      this.gear = new BehaviorSubject(data);
    });
  }

  getBrandList() {
    this.service.getBrandList().subscribe(data => {
      this.brands = new BehaviorSubject(data);
    });
  }

  onPageChange(event) {
    this.page = event.first;
    this.paginate();
  }

  onSortChange(event) {
    let value = event.value;

    if (value.indexOf('!') === 0) {
      this.sortOrder = -1;
      this.sortField = value.substring(1, value.length);
    } else {
      this.sortOrder = 1;
      this.sortField = value;
    }
  }

  paginate() {
    this.service.paginate(this.page, this.pageSize, this.name).subscribe(data => {
      // console.log(data);
      this.cars$ = new BehaviorSubject(data.content);
      this.total$ = new BehaviorSubject(data.totalPages);
    }, error => {
      console.log(error);
    });
  }

  openNew() {
    this.car = {
      status: "",
      description: "",
      brandName: ""
    };
    this.submitted = false;
    this.productDialog = true;
  }

  deleteSelectedProducts() {
    this.confirmationService.confirm({
      message: 'Are you sure you want to delete the selected products?',
      header: 'Confirm',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.cars = this.cars.filter(val => !this.selectedProducts.includes(val));
        this.selectedProducts = null;
        this.messageService.add({
          severity: 'success',
          summary: 'Successful',
          detail: 'Products Deleted',
          life: 3000
        });
      }
    });
  }

  editProduct(product) {
    this.car = Object.assign({}, product);
    this.productDialog = true;
  }

  deleteProduct(product) {
    this.confirmationService.confirm({
      message: 'Are you sure you want to delete ' + product.name + '?',
      header: 'Confirm',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.service.deleteCar(parseInt(product.id)).subscribe(() => {
          // console.log(product.id)
          this.paginate();
          this.messageService.add({
            severity: 'success',
            summary: 'Successful',
            detail: 'Product Deleted',
            life: 3000
          });
        });
      }
    });
  }

  hideDialog() {
    this.productDialog = false;
    this.submitted = false;
  }

  saveProduct() {
    this.submitted = true;

    if (this.car.name.trim()) {
      if (this.car.id) {
        this.cars[this.findIndexById(this.car.id)] = this.car;
        this.messageService.add({
          severity: 'success',
          summary: 'Successful',
          detail: 'Product Updated',
          life: 3000
        });
      } else {
        this.car.id = this.createId();
        this.car.picture = 'product-placeholder.svg';
        this.cars.push(this.car);
        this.messageService.add({
          severity: 'success',
          summary: 'Successful',
          detail: 'Product Created',
          life: 3000
        });
      }

      this.cars = [...this.cars];
      this.productDialog = false;
      this.car = {
        description: "",
        status: ""
      };
    }
  }

  findIndexById(id) {
    let index = -1;

    for (let i = 0; i < this.cars.length; i++) {
      if (this.cars[i].id === id) {
        index = i;
        break;
      }
    }

    return index;
  }

  createId() {
    let id = '';
    var chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

    for (var i = 0; i < 5; i++) {
      id += chars.charAt(Math.floor(Math.random() * chars.length));
    }

    return id;
  }

}

