export class CarListComponent {
  constructor(service, primengConfig, messageService, confirmationService) {
    this.service = service;
    this.primengConfig = primengConfig;
    this.messageService = messageService;
    this.confirmationService = confirmationService;
    this.page = 0;
    this.pageSize = 10;
    this.name = '';
  }

  ngOnInit() {
    console.log('data');
    this.paginate();
    this.sortOptions = [{
      label: 'Price High to Low',
      value: '!price'
    }, {
      label: 'Price Low to High',
      value: 'price'
    }];
  }

  onPageChange(event) {
    this.page = event.first;
    this.paginate();
  }

  onSortChange(event) {
    let value = event.value;

    if (value.indexOf('!') === 0) {
      this.sortOrder = -1;
      this.sortField = value.substring(1, value.length);
    } else {
      this.sortOrder = 1;
      this.sortField = value;
    }
  }

  paginate() {
    this.service.paginate(this.page, this.pageSize, this.name).subscribe(data => {
      console.log(data);
      this.cars$ = new BehaviorSubject(data.content);
      this.total$ = new BehaviorSubject(data.totalElements);
      this.totalElements = data.totalElements;
    }, error => {
      console.log(error);
    });
  }

  openNew() {
    this.product = {
      status: "",
      description: "",
      brandName: ""
    };
    this.submitted = false;
    this.productDialog = true;
  }

  deleteSelectedProducts() {
    this.confirmationService.confirm({
      message: 'Are you sure you want to delete the selected products?',
      header: 'Confirm',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.products = this.products.filter(val => !this.selectedProducts.includes(val));
        this.selectedProducts = null;
        this.messageService.add({
          severity: 'success',
          summary: 'Successful',
          detail: 'Products Deleted',
          life: 3000
        });
      }
    });
  }

  editProduct(product) {
    this.product = Object.assign({}, product);
    this.productDialog = true;
  }

  deleteProduct(product) {
    this.confirmationService.confirm({
      message: 'Are you sure you want to delete ' + product.name + '?',
      header: 'Confirm',
      icon: 'pi pi-exclamation-triangle',
      accept: () => {
        this.products = this.products.filter(val => val.id !== product.id);
        this.product = {
          description: "",
          status: ""
        };
        this.messageService.add({
          severity: 'success',
          summary: 'Successful',
          detail: 'Product Deleted',
          life: 3000
        });
      }
    });
  }

  hideDialog() {
    this.productDialog = false;
    this.submitted = false;
  }

  saveProduct() {
    this.submitted = true;

    if (this.product.name.trim()) {
      if (this.product.id) {
        this.products[this.findIndexById(this.product.id)] = this.product;
        this.messageService.add({
          severity: 'success',
          summary: 'Successful',
          detail: 'Product Updated',
          life: 3000
        });
      } else {
        this.product.id = this.createId();
        this.product.picture = 'product-placeholder.svg';
        this.products.push(this.product);
        this.messageService.add({
          severity: 'success',
          summary: 'Successful',
          detail: 'Product Created',
          life: 3000
        });
      }

      this.products = [...this.products];
      this.productDialog = false;
      this.product = {
        description: "",
        status: ""
      };
    }
  }

  findIndexById(id) {
    let index = -1;

    for (let i = 0; i < this.products.length; i++) {
      if (this.products[i].id === id) {
        index = i;
        break;
      }
    }

    return index;
  }

  createId() {
    let id = '';
    var chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

    for (var i = 0; i < 5; i++) {
      id += chars.charAt(Math.floor(Math.random() * chars.length));
    }

    return id;
  }

}

