export class HomePageComponent {
  constructor(service, primengConfig) {
    this.service = service;
    this.primengConfig = primengConfig;
    this.pageNumber = 1;
    this.pageSize = 5;
    this.name = '';
  }

  ngOnInit() {
    console.log('data');
    this.paginate();
    this.sortOptions = [{
      label: 'Price High to Low',
      value: '!price'
    }, {
      label: 'Price Low to High',
      value: 'price'
    }];
  }

  onSortChange(event) {
    let value = event.value;

    if (value.indexOf('!') === 0) {
      this.sortOrder = -1;
      this.sortField = value.substring(1, value.length);
    } else {
      this.sortOrder = 1;
      this.sortField = value;
    }
  }

  paginate() {
    this.service.paginate(this.pageNumber, this.pageSize, this.name).subscribe(data => {
      console.log(data);
      this.allCars = new BehaviorSubject(data.content);
      this.total$ = new BehaviorSubject(data.totalElements);
      this.totalElements = data.totalElements;
    }, error => {
      console.log(error);
    });
  }

}

